class Solution:
    def longestDupSubstring(self, s: str) -> str:
        l, r = 1, len(s)
        while l < r:
            cur = (l + r) // 2
            if self.search(s, cur):
                l = cur + 1
            else:
                r = cur
        start = self.search(s, l - 1)
        return s[start:start + l - 1]

    def search(self, s, L):
        start = 0
        hash_set = set()
        modulus = 2 ** 32

        enc = 0
        for c in s[0:L]:
            enc = enc * 26 + (ord(c) - ord('a'))
        hash_set.add(enc % modulus)

        for e in range(L, len(s)):
            rem = ord(s[e - L]) - ord('a')
            add = ord(s[e]) - ord('a')
            enc = enc - rem * (26 ** (L - 1))
            enc = enc * 26 + add
            enc = enc % modulus
            if enc in hash_set:
                start = e - L + 1
                break
            hash_set.add(enc)
        return start

if __name__=="__main__":
    solution = Solution()
    s = ""
    print(solution.longestDupSubstring(""))